INSERT INTO `cs_article` VALUES ('148', '69', '2', 'article/picture/2013/09/IA6vPc1L01378478865.jpg', '北京80后女画家李雅鑫举行画展婚礼', null, '2013年9月1日上午，京城建国路的外交文化交流中心迎来一场别开生面的情艺画展婚礼。画展的主人也是今天婚礼的新娘青年女画家李雅鑫女士。在这场情艺的殿堂上，展出了新娘李雅鑫近年创作的60余幅精品力作，画展与婚礼的交融盛典也见证了李雅鑫女士事业与爱情的双丰收。', '视觉中国', null, null, null, null, '1378478865', '5', '0', '286', '0', '1', '1', '0', '0', '0', '0');
