INSERT INTO `cs_article` VALUES ('224', '107', '8', null, '新华网申请IPO被受理 已获得文化产业基金投资', null, '继人民网股份有限公司在去年登陆上证所后，另一“国家队”新闻网站--新华网股份有限公司（下称“新华网”）征战资本市场在2013年年初也迈出了关键的一步。\r\n\r\n\r\n　　根据证监会网站最新公布的首次公开发行（IPO）股票申报企业基本信息情况表（下称“信息表”），新华网首次出现在待审企业名单中，显示其IPO申请已经获得证监会受理，拟上市地为上海证券交易所（上证所）。 \r\n\r\n　　此前的2011年底，路透', '东方早报', null, null, null, null, '1357637180', '0', '0', '304', '0', '1', '1', '0', '0', '0', '0');
